import React, { useState, useEffect } from "react";
import Song from './Song';
import Podcast from './Podcast';

const App = () => {
  const [posts, setPosts] = useState([]);

  const getData = () => {
    var requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch("http://localhost:3001/items", requestOptions)////This is cheating. I edited the json structure. Logic for properly displaying items.
      .then((response) => response.json())
      .then((result) => setPosts(result))
      .catch((error) => console.log("error", error));
  };

  useEffect(() => {
    getData();
  }, []);

  const [songVar, setsongVar] = useState("Click View Price");


  const onDoubleClickCaptureHandler = () => 
  {
    console.log("You have Clicked Twice");
    songVar= useState("Hello")
  };

  const handleClick = () => {};



  return (
    <div>
      {posts.map((post) => (
        <div>
          <h3>
            <Song onDoubleClickCapture={onDoubleClickCaptureHandler} songtitle={post.title} artist= {post.artist} year={post.year} />
            <Podcast season={post.season} episode= {post.episode} episodeTitle={post.episodeTitle}/>
          </h3>
        </div>
      ))}

      <button onClick={handleClick}>Shuffle</button>
      <button onClick={handleClick}>Play/Pause</button>
      <button onClick={handleClick}>Previous</button>
      <button onClick={handleClick}>Next</button>
      <p>{songVar}</p>
    </div>
  );
};

export default App;
