import React, { useState, useEffect } from "react";
import './ExternalStylesheet.css';
import Song from './Song';
import Podcast from './Podcast';

const App = () => {
  const [posts, setPosts] = useState([]);

  const getData = () => {
    var requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch("http://localhost:3001/tracks", requestOptions)
      .then((response) => response.json())
      .then((result) => setPosts(result))
      .catch((error) => console.log("error", error));
  };

  useEffect(() => {
    getData();
  }, []);


  //Add ids
  for(var i=0; i < posts.length; i++)
  {
    posts[i].trackid= i
  }


  ///****Hooks/states */
  const [title, setTitle] = useState("Playing: Billie Jean");
  const [idx, setIdx] = useState(0);
  //const [play, setPlaying] = useState(false);

  //create a hook to set id for incrementing/decrementing
  //states?? for playing

  //const onDoubleClickCaptureHandler = event => setTitle("Changed text");

  /*
  const onDoubleClickCaptureHandler = () => 
  {
    console.log("You have Clicked Twice");
    songVar="Hello"
  };
  */






  ///***Assignment 2 lists and mapping */
  //***Apply logic here */
  //var arr = []


  //logic done in components

  //const listItems = arr.map(item => <li>{item}</li>);
  //<ul style={{ listStyleType: "none" }}>{listItems}</ul>




  var arr = []
  var arr0 = []
  //might need to nest these to get things to work

  //json to array
  for(var j=0; j < posts.length; j++)
  {
    arr0.push(Object.values(posts[j]));
  }

  //Nested
  /*

  for (let i= 0; i < posts.length; i += 1) {
    const element = posts[i];
    Object.values(element).forEach((r) => { arr.push(r) });
 }
 */


  const handleClick = () => {setTitle("Shuffle")};
  const handleClick2 = () => {setTitle("Paused: "+arr0[idx])};

  //Possible if statements for <0 and >posts.length

  const handleClick3 = () => {setIdx(idx-1)
                              if(idx===0){setIdx(posts.length-1); setTitle(arr0[posts.length-1]) }
                              else{setTitle(arr0[idx-1])};//Json[id].title}
  }

  const handleClick4 = () => {setIdx(idx+1)
                              if(idx>posts.length-2){setIdx(0); setTitle(arr0[0]) }

                              else{setTitle(arr0[idx+1])};}





    //*** */
  //Implemnent doubleclick method to set idx and title
  //*** */

 //Create title array for nexr/prev from array from json
 //Just do the whole messy thing for now



 //try to create ids when mapping in return to increment/decrement

 //Or do the mapping and array here and create ids

 //The song or podcast components are wrapped in an h3
  /*
 function someFunc() {
    setTitle("Playing: Current ID")
    setIdx(3)
    //Loop through arr0 searching for a match
  }
  */




  ///need an array and logic outside of the return function
  //with ids





  //title or episodetitle
  return (
    <div>
      <h1>{arr0[0]}</h1>
      <h1>{arr[0]}</h1>

      {posts.map((post) => (
        <div>
          <h3 onDoubleClick={() => {setIdx(post.trackid);setTitle(post.title+post.episodeTitle)}}>
            <Song songtitle={post.title} artist= {post.artist} year={post.year} />
            <Podcast season={post.season} episode= {post.episode} episodeTitle={post.episodeTitle}/>
            <p>{post.trackid}</p>
          </h3>
        </div>
      ))}

      <button onClick={handleClick}>Shuffle</button>
      <button onClick={handleClick2}>Play/Pause</button>
      <button onClick={handleClick3}>Previous</button>
      <button onClick={handleClick4}>Next</button>
      <p>{idx}</p>
      <p>{title}</p>
    </div>
  );
};

export default App;
