//import logo from './logo.svg';
import './ExternalStylesheet.css';
import Song from './Song';
import Podcast from './Podcast';
import AudioData from './audio.json'



export default function Playlist() 
{
  var arr =  []

  //use fetch and http endpoint instead of local implementation
  //must be in proper folder to display index page

  const getItemsFromApi = () => {
  return fetch('https://localhost:3001')
    .then(response => response.json())
    .then(json => {
      return json.items;
    })
    .catch(error => {
      console.error(error);
    });
};



  {AudioData.map((audioDetail, index)=>{
    if(audioDetail.title)
      {
        //return <h1>{audioDetail.title}</h1>
        arr.push(<Song songtitle={audioDetail.title} artist= {audioDetail.artist} year={audioDetail.year}/>)
        //arr.push title only??
      }
    else
    {
      //return <h1>{audioDetail.episodeTitle}</h1>
      arr.push(<Podcast season={audioDetail.season} episode= {audioDetail.episode} episodeTitle={audioDetail.episodeTitle}/>)

    }



  })}

  const listItems = arr.map(item => <li>{item}</li>);
  const listItems2 = getItemsFromApi.map(item => <li>{item}</li>);


  const handleClick = () => {};

  return (
    <div>
      <ul style={{ listStyleType: "none" }}>{listItems}</ul>
      <ul style={{ listStyleType: "none" }}>{listItems2}</ul>
      <button onClick={handleClick}>Shuffle</button>
      <button onClick={handleClick}>Play/Pause</button>
      <button onClick={handleClick}>Previous</button>
      <button onClick={handleClick}>Next</button>
      <p>Status Display</p>
    </div>
  );




}
